import React, { useState, useEffect } from 'react';
import useAPI from '../../services/api';
import {Link} from 'react-router-dom';
import  './styles.css';
import API from '../../services/api';
import { doLogin } from '../../services/AuthHandler';
import {ErrorMessage} from '../../components/MainComponents';


const Login = () => {

    const API = useAPI();

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rememberPassword, setRememberPassword] = useState(false);
    const [token, setToken] = useState('');
    const [error, setError] = useState('');


    const handleSubmit = async (e) => {
        e.preventDefault();

        const json = await api.login(email, password);

        if(json.error) {
            setError(json.error);
        } else {
            doLogin(json.token, rememberPassword );
            window.location.href = '/dashboard';
        }

    }



    return (
        <div className="login">
            <h1 className="login__titulo">Acessar Sistema</h1>
               {error &&
                    <ErrorMessage>{error}</ErrorMessage>
               } 

            <form onSubmit={handleSubmit}>
                <div className="login__form">
                    <input 
                    id="email" 
                    type="email" 
                    name="email"
                    placeholder="Digite seu e-mail" 
                    value={email}
                    onChange={(e)=>setEmail(e.target.value)}
                    />
                </div>
                <div className="login__form">
                    <input 
                    id="password" 
                    type="password" 
                    name="password"
                    placeholder="Digite sua senha" 
                    value={password}
                    onChange={(e)=>setPassword(e.target.value)}
                    /> 
                </div>
            </form>
            <button className="login__button" onClick={handleSubmit}>Entrar</button>
            <div className="login__cad">
                <Link to="/cadastro" className="login__cadtext">Ainda n√£o possui conta? Cadastre-se</Link>
            </div>
        </div>

    );
}

export default Login;
