import React, { useState } from 'react';
import Menu from '../../components/menu';
import axios from 'axios'
import './styles.css'
import Cookies from 'js-cookie';

function Dashboard() {

    const [list, setList] = useState([]);
    const [name, setName] = useState(Cookies.get('name'));
    const [avatar, setAvatar] = useState(Cookies.get('avatar'));

    const handlelogout = () => {
        return(
            Cookies.remove('token'),
            window.location.href='/'
        );
    }
//REQUISITANDO A MINHA API
    const loadlist = () => {
        axios.get('http://127.0.0.1:8000/api/users')
            .then(function(response){
                console.log(response);
                setList(response.data.result);
            });
    }

    function Avatar(props) {
        return(
            <div>
                <img className="foto" src={props.url} />
            </div>
        );
    }


    return(
        <div>
            <Menu />
            <Avatar url="http://127.0.0.1:8001/media/avatars/default.png" />
            <h1 className="title">Dashboard</h1>
            <h3 className="msg">Ol√° {name} {avatar}!</h3>
                <ul className="agnda">
                    {list.map((item, index)=>(
                        <li key={index}>{item.title}</li>
                    ))}
                </ul>
            <button onClick={handlelogout}>Sair</button>
            <button onClick={loadlist}>agendamentos</button>
        </div>
    );
}

export default Dashboard;
