import React, { useState } from 'react';
import api from '../../services/api'

import  './styles.css';
import {Link} from 'react-router-dom';


const Cadastro = () => {


    const [name, setName] = useState('');
    const [stateLoc, setStateLoc] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const handleLogin = async () => {
        const response = await api.post('/barber', {
            name,
            email,
            password
        });
        console.log(response);
    };

    return (
        <div className="login">
            <h1 className="login__titulo">Acessar Sistema</h1>
            <form>
                <div className="login__form">
                    <input 
                    id="name" 
                    type="name" 
                    name="name"
                    placeholder="Digite seu nome" 
                    value={name}
                    onChange={(e)=>setName(e.target.value)}
                    required
                    />
                </div>
                <div className="login__form">
                    <select value={stateLoc} onChange={e=>setStateLoc(e.target.value)} require>
                        <option></option>
                    </select>
                </div>
                <div className="login__form">
                    <input 
                    id="email" 
                    type="email" 
                    name="email"
                    placeholder="Digite sua email" 
                    value={email}
                    onChange={(e)=>setEmail(e.target.value)}
                    required
                    /> 
                </div>
                <div className="login__form">
                    <input 
                    id="password" 
                    type="password" 
                    name="password"
                    placeholder="Digite sua senha" 
                    value={password}
                    onChange={(e)=>setPassword(e.target.value)}
                    required
                    /> 
                </div>
                <div className="login__form">
                    <input 
                    id="password" 
                    type="password" 
                    name="password"
                    placeholder="Confirme sua senha" 
                    value={confirmPassword}
                    onChange={(e)=>setConfirmPassword(e.target.value)}
                    required
                    /> 
                </div>
            </form>
            <button className="login__button" onClick={handleLogin}>CADASTRAR</button>
            <div className="login__cad">
                <Link to="/" className="login__cadtext">Já possui uma conta? Faça login</Link>
            </div>
        </div>
    );
}

export default Cadastro;
