import React, { useState } from 'react';
import api from '../../services/api'
import Header from '../../components/header';
import  './styles.css';


const Login = () => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async () => {
        const response = await api.post('/auth/login', {
            email,
            password
        });
        console.log(response.data);
    };

    return (
        <div className="login">
        <Header />
        <h1 className="login__titulo">Acessar Sistema</h1>
        <form>
            <div className="login__form">
                <input 
                id="email" 
                type="email" 
                name="email"
                placeholder="Digite seu e-mail" 
                value={email}
                onChange={(e)=>setEmail(e.target.value)}
                />
            </div>
            <div className="login__form">
                <input 
                id="password" 
                type="password" 
                name="password"
                placeholder="Digite sua senha" 
                value={password}
                onChange={(e)=>setPassword(e.target.value)}
                /> 
            </div>
        </form>
        <button className="login__button" onClick={handleLogin}>Entrar</button>
        <div className="login__cad">
            <a href="" className="login__cadtext">Ainda n√£o possui conta? Cadastre-se</a>
        </div>
    </div>

    );
}

export default Login;
